var timerToast;
// TOASTS E ALERTAS
function toast( ty, ti, tx ) {
	// success = 1
	// info = 2
	// alerta = 3
	// erro = 4
	closeToast();
	
	setTimeout(function(){
	$(".toastc div").css("display","none");
	$(".toasti div").css("display","none");
	$(".toastt h4").css("display","none");
	
	$(".toastc"+ty).css("display","block");
	$(".toasti"+ty).css("display","block");
	
	if ( ti != "" ) {
		$(".toastt h4").css("display","block");
	}
	
	$(".toastt h4").html(ti);
	$(".toastt p").html(tx);
	
	$(".toast").css("top","10px");
	
	timeToast();
	},150);
}

function timeToast() {
	timerToast = setTimeout(closeToast,15000);
}
	
function closeToast(){
	$(".toast").css("top","-300px");
	clearTimeout(timerToast);
}